    {i: 0, n: "全部字数"},
    {i: 11, n: "100万字以下"},
    {i: 12, n: "200万字以下"},
];

let girlRank = [
    {i: 'hour72', n: "🔥人气榜"},
    {i: 'potential', n: "🆕新书榜"},
    {i: 'votes', n: "💰银票榜"},
    {i: 'wholebook', n: "📚完本榜"},
];

let boyRank = [
    {i: 'hour72', n: "🔥人气榜"},
    {i: 'potential', n: "🆕新书榜"},
    {i: 'votes', n: "💰银票榜"},
