                title: type.n,
                url: `/book/rank/list/0-${rank.i}-${type.i}-${s.i}-{{page}}`,
                style: defaultStyle
            });
        }
    }
}
for (let rank of girlRank) {
    for (let s of sort) {
        let title = `🔸 ♀️女频·${rank.n}·${s.n}·可点击 🔸`;
        result.push({
            title: title,
            url: `/book/rank/list/3-${rank.i}-0-${s.i}-{{page}}`,
            style: titleStyle
        });
        for (let type of girlTypes) {
            result.push({
                title: type.n,
                url: `/book/rank/list/3-${rank.i}-${type.i}-${s.i}-{{page}}`,
                style: defaultStyle
            });
        }
    }
}
JSON.stringify(result);

